@extends('layouts.app')\n\n@section('title', 'Loading Indicator Examples')\n\n@section('content')\n<div class=\"max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8\">\n    <!-- Header -->\n    <div class=\"text-center mb-12\">\n        <h1 class=\"text-3xl font-bold text-gray-900 dark:text-white mb-4\">\n            Loading Indicator Examples\n        </h1>\n        <p class=\"text-lg text-gray-600 dark:text-gray-400\">\n            Interactive examples showing how to use the StudEats loading indicator system\n        </p>\n    </div>\n\n    <div class=\"space-y-8\">\n        <!-- Basic Examples -->\n        <section class=\"bg-white dark:bg-gray-800 rounded-lg shadow-md p-6\">\n            <h2 class=\"text-xl font-semibold mb-4 text-gray-900 dark:text-white\">Basic Loading Indicators</h2>\n            <div class=\"grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4\">\n                <div class=\"text-center\">\n                    <h3 class=\"font-medium mb-2 text-gray-700 dark:text-gray-300\">Spinner</h3>\n                    <div id=\"spinner-demo\" class=\"min-h-[100px] flex items-center justify-center border-2 border-dashed border-gray-300 rounded-lg\"></div>\n                    <button onclick=\"toggleSpinner()\" class=\"mt-2 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors\">\n                        Toggle\n                    </button>\n                </div>\n                \n                <div class=\"text-center\">\n                    <h3 class=\"font-medium mb-2 text-gray-700 dark:text-gray-300\">Dots</h3>\n                    <div id=\"dots-demo\" class=\"min-h-[100px] flex items-center justify-center border-2 border-dashed border-gray-300 rounded-lg\"></div>\n                    <button onclick=\"toggleDots()\" class=\"mt-2 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition-colors\">\n                        Toggle\n                    </button>\n                </div>\n                \n                <div class=\"text-center\">\n                    <h3 class=\"font-medium mb-2 text-gray-700 dark:text-gray-300\">Pulse</h3>\n                    <div id=\"pulse-demo\" class=\"min-h-[100px] flex items-center justify-center border-2 border-dashed border-gray-300 rounded-lg\"></div>\n                    <button onclick=\"togglePulse()\" class=\"mt-2 px-4 py-2 bg-purple-600 text-white rounded hover:bg-purple-700 transition-colors\">\n                        Toggle\n                    </button>\n                </div>\n                \n                <div class=\"text-center\">\n                    <h3 class=\"font-medium mb-2 text-gray-700 dark:text-gray-300\">Progress</h3>\n                    <div id=\"progress-demo\" class=\"min-h-[100px] flex items-center justify-center border-2 border-dashed border-gray-300 rounded-lg\"></div>\n                    <button onclick=\"toggleProgress()\" class=\"mt-2 px-4 py-2 bg-orange-600 text-white rounded hover:bg-orange-700 transition-colors\">\n                        Toggle\n                    </button>\n                </div>\n            </div>\n        </section>\n\n        <!-- Button Loading -->\n        <section class=\"bg-white dark:bg-gray-800 rounded-lg shadow-md p-6\">\n            <h2 class=\"text-xl font-semibold mb-4 text-gray-900 dark:text-white\">Button Loading States</h2>\n            <div class=\"grid grid-cols-1 md:grid-cols-3 gap-4\">\n                <button id=\"demo-button-1\" onclick=\"showButtonLoading('#demo-button-1')\" class=\"px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors\">\n                    Process Data\n                </button>\n                <button id=\"demo-button-2\" onclick=\"showButtonLoading('#demo-button-2')\" class=\"px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors\">\n                    Save Recipe\n                </button>\n                <button id=\"demo-button-3\" onclick=\"showButtonLoading('#demo-button-3')\" class=\"px-6 py-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors\">\n                    Generate Plan\n                </button>\n            </div>\n        </section>\n\n        <!-- Overlay Examples -->\n        <section class=\"bg-white dark:bg-gray-800 rounded-lg shadow-md p-6\">\n            <h2 class=\"text-xl font-semibold mb-4 text-gray-900 dark:text-white\">Full-Screen Overlays</h2>\n            <div class=\"grid grid-cols-1 md:grid-cols-2 gap-4\">\n                <button onclick=\"showMealPlanningOverlay()\" class=\"px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors\">\n                    Meal Planning Process\n                </button>\n                <button onclick=\"showRecipeProcessingOverlay()\" class=\"px-6 py-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors\">\n                    Recipe Processing\n                </button>\n            </div>\n        </section>\n\n        <!-- State Manager Demo -->\n        <section class=\"bg-white dark:bg-gray-800 rounded-lg shadow-md p-6\">\n            <h2 class=\"text-xl font-semibold mb-4 text-gray-900 dark:text-white\">Multi-Stage Operations</h2>\n            <div class=\"space-y-4\">\n                <div id=\"state-demo-container\" class=\"min-h-[120px] border-2 border-dashed border-gray-300 rounded-lg flex items-center justify-center\">\n                    <p class=\"text-gray-500\">Click button below to start multi-stage operation</p>\n                </div>\n                <div class=\"flex gap-4\">\n                    <button onclick=\"startMultiStageDemo()\" class=\"px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors\">\n                        Start Multi-Stage Operation\n                    </button>\n                    <button onclick=\"stopDemo()\" class=\"px-6 py-3 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors\">\n                        Cancel Operation\n                    </button>\n                </div>\n                <div id=\"operation-status\" class=\"text-sm text-gray-600 dark:text-gray-400\"></div>\n            </div>\n        </section>\n\n        <!-- Practical Example -->\n        <section class=\"bg-white dark:bg-gray-800 rounded-lg shadow-md p-6\">\n            <h2 class=\"text-xl font-semibold mb-4 text-gray-900 dark:text-white\">Practical Example: Simulate API Call</h2>\n            <form id=\"demo-form\" class=\"space-y-4\">\n                <div>\n                    <label for=\"demo-input\" class=\"block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2\">\n                        Enter recipe name:\n                    </label>\n                    <input type=\"text\" id=\"demo-input\" class=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500\" placeholder=\"e.g., Chicken Adobo\">\n                </div>\n                <button type=\"submit\" class=\"px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors\">\n                    Process Recipe\n                </button>\n            </form>\n            <div id=\"demo-result\" class=\"mt-4 hidden p-4 bg-green-50 border border-green-200 rounded-lg\">\n                <p class=\"text-green-800\">Recipe processed successfully!</p>\n            </div>\n        </section>\n\n        <!-- Code Examples -->\n        <section class=\"bg-white dark:bg-gray-800 rounded-lg shadow-md p-6\">\n            <h2 class=\"text-xl font-semibold mb-4 text-gray-900 dark:text-white\">Code Examples</h2>\n            <div class=\"space-y-4\">\n                <div>\n                    <h3 class=\"font-medium mb-2 text-gray-700 dark:text-gray-300\">Show Button Loading</h3>\n                    <pre class=\"bg-gray-100 dark:bg-gray-900 p-4 rounded-lg overflow-x-auto text-sm\"><code>const restoreButton = LoadingUtils.showOnButton('#my-button', {\n    text: 'Processing...',\n    size: 'medium'\n});\n\n// Later restore the button\nrestoreButton();</code></pre>\n                </div>\n                \n                <div>\n                    <h3 class=\"font-medium mb-2 text-gray-700 dark:text-gray-300\">Show Overlay</h3>\n                    <pre class=\"bg-gray-100 dark:bg-gray-900 p-4 rounded-lg overflow-x-auto text-sm\"><code>const overlay = LoadingUtils.showOverlay({\n    messages: LoadingMessages.MEAL_PLANNING,\n    variant: 'spinner',\n    size: 'large'\n});\n\n// Hide overlay\noverlay.hide();</code></pre>\n                </div>\n                \n                <div>\n                    <h3 class=\"font-medium mb-2 text-gray-700 dark:text-gray-300\">State Manager</h3>\n                    <pre class=\"bg-gray-100 dark:bg-gray-900 p-4 rounded-lg overflow-x-auto text-sm\"><code>const stateManager = LoadingUtils.createStateManager({\n    messages: LoadingMessages.RECIPE_PROCESSING,\n    minLoadingTime: 1000\n});\n\n// Use with async operation\nconst result = await stateManager.withLoading(\n    async () => {\n        return await fetch('/api/data').then(r => r.json());\n    },\n    '#container'\n);</code></pre>\n                </div>\n            </div>\n        </section>\n    </div>\n</div>\n\n<script>\n// Global variables for demo\nlet demoLoaders = {};\nlet stateManager = null;\n\n// Basic variant toggles\nfunction toggleSpinner() {\n    toggleLoader('spinner', 'spinner-demo', { variant: 'spinner' });\n}\n\nfunction toggleDots() {\n    toggleLoader('dots', 'dots-demo', { variant: 'dots' });\n}\n\nfunction togglePulse() {\n    toggleLoader('pulse', 'pulse-demo', { variant: 'pulse' });\n}\n\nfunction toggleProgress() {\n    toggleLoader('progress', 'progress-demo', { variant: 'progress' });\n}\n\n// Generic toggle function\nfunction toggleLoader(key, containerId, options) {\n    if (demoLoaders[key]) {\n        demoLoaders[key].destroy();\n        delete demoLoaders[key];\n    } else {\n        const container = document.getElementById(containerId);\n        demoLoaders[key] = new LoadingIndicator({\n            container: container,\n            ...options\n        });\n        demoLoaders[key].show();\n    }\n}\n\n// Button loading demo\nfunction showButtonLoading(buttonSelector) {\n    const restoreButton = LoadingUtils.showOnButton(buttonSelector, {\n        text: 'Processing...',\n        size: 'medium'\n    });\n\n    // Restore after 3 seconds\n    setTimeout(() => {\n        restoreButton();\n    }, 3000);\n}\n\n// Overlay demos\nfunction showMealPlanningOverlay() {\n    const overlay = LoadingUtils.showOverlay({\n        messages: LoadingMessages.MEAL_PLANNING,\n        variant: 'spinner',\n        size: 'large'\n    });\n\n    // Auto-hide after 5 seconds\n    setTimeout(() => {\n        overlay.hide();\n    }, 5000);\n}\n\nfunction showRecipeProcessingOverlay() {\n    const overlay = LoadingUtils.showOverlay({\n        messages: LoadingMessages.RECIPE_PROCESSING,\n        variant: 'spinner',\n        size: 'large'\n    });\n\n    // Auto-hide after 5 seconds\n    setTimeout(() => {\n        overlay.hide();\n    }, 5000);\n}\n\n// State manager demo\nfunction startMultiStageDemo() {\n    if (stateManager && stateManager.isLoading) return;\n\n    stateManager = LoadingUtils.createStateManager({\n        messages: [\n            \"Step 1: Validating input...\",\n            \"Step 2: Processing data...\",\n            \"Step 3: Calculating results...\",\n            \"Step 4: Finalizing...\",\n            \"Complete!\"\n        ],\n        minLoadingTime: 1000,\n        onStageComplete: (stage) => {\n            updateOperationStatus(`Stage ${stage + 1} completed`);\n        },\n        onComplete: () => {\n            updateOperationStatus('Operation completed successfully!');\n        }\n    });\n\n    simulateMultiStageOperation();\n}\n\nasync function simulateMultiStageOperation() {\n    stateManager.start('#state-demo-container');\n    updateOperationStatus('Starting multi-stage operation...');\n\n    for (let i = 0; i < 4; i++) {\n        await new Promise(resolve => setTimeout(resolve, 1500));\n        stateManager.nextStage();\n    }\n\n    await stateManager.stop();\n}\n\nfunction stopDemo() {\n    if (stateManager && stateManager.isLoading) {\n        stateManager.stop();\n        updateOperationStatus('Operation cancelled!');\n    }\n}\n\nfunction updateOperationStatus(message) {\n    const statusEl = document.getElementById('operation-status');\n    if (statusEl) {\n        statusEl.textContent = message;\n    }\n}\n\n// Practical form demo\ndocument.getElementById('demo-form').addEventListener('submit', async function(e) {\n    e.preventDefault();\n    \n    const input = document.getElementById('demo-input');\n    const result = document.getElementById('demo-result');\n    \n    if (!input.value.trim()) {\n        alert('Please enter a recipe name');\n        return;\n    }\n    \n    // Show loading state\n    const restoreButton = LoadingUtils.showOnButton('button[type=\"submit\"]', {\n        text: 'Processing Recipe...',\n        size: 'medium'\n    });\n    \n    const overlay = LoadingUtils.showOverlay({\n        messages: LoadingMessages.RECIPE_PROCESSING,\n        variant: 'spinner',\n        size: 'medium'\n    });\n    \n    // Simulate API call\n    await new Promise(resolve => setTimeout(resolve, 3000));\n    \n    // Hide loading and show result\n    overlay.hide();\n    restoreButton();\n    result.classList.remove('hidden');\n    \n    // Hide result after 3 seconds\n    setTimeout(() => {\n        result.classList.add('hidden');\n        input.value = '';\n    }, 3000);\n});\n</script>\n@endsection